import { useContext, useEffect, useState } from "react";
import { AppContext } from "../../context/AppContext";
import { fetchBudget, updateBudget } from "../../utils/budget-utils";

const Budget = () => {
    const { budget, setBudget } = useContext(AppContext);
    const [isEditing, setIsEditing] = useState(false);
    const [newBudget, setNewBudget] = useState(budget.toString());
    const [error, setError] = useState<string | null>(null);

    // Fetch initial budget on component mount
    useEffect(() => {
        const loadBudget = async () => {
            try {
                const budgetAmount = await fetchBudget();
                setBudget(budgetAmount);
                setNewBudget(budgetAmount.toString());
                setError(null);
            } catch (err) {
                setError('Failed to load budget');
                console.error('Failed to load budget:', err);
            }
        };

        loadBudget();
    }, []); // Only run on mount

    const handleEditClick = async () => {
        if (isEditing) {
            try {
                const budgetValue = parseFloat(newBudget);
                if (isNaN(budgetValue) || budgetValue <= 0) {
                    setError('Please enter a valid positive number');
                    return;
                }

                const updatedBudget = await updateBudget(budgetValue);
                setBudget(updatedBudget);
                setError(null);
                setIsEditing(false);
            } catch (err) {
                setError('Failed to update budget');
                console.error('Failed to update budget:', err);
                return;
            }
        } else {
            setIsEditing(true);
        }
    };

    return (
        <div className="alert alert-secondary">
            {error && (
                <div className="alert alert-danger mb-2" role="alert">
                    {error}
                </div>
            )}
            <div className="d-flex align-items-center justify-content-between">
                {isEditing ? (
                    <input
                        data-testid="budget-input"
                        type="number"
                        className="form-control"
                        value={newBudget}
                        onChange={(e) => {
                            setNewBudget(e.target.value);
                            setError(null);
                        }}
                        min="0"
                        step="0.01"
                    />
                ) : (
                    <div data-testid="budget-display">Budget: ${budget}</div>
                )}
                <button
                    className={`btn btn-${isEditing ? 'success' : 'primary'} ms-2`}
                    onClick={handleEditClick}
                    aria-label={isEditing ? 'Save budget' : 'Edit budget'}
                >
                    {isEditing ? 'Save' : 'Edit'}
                </button>
            </div>
        </div>
    );
};

export default Budget;
